function kml_to_poligon_center(kml, position) {
    // Transforms string coordinates to well structured poligon and center coordinates
    var farm = kml.Document[0].Placemark[position];

    var center_point = {
        "lat": parseFloat(farm.LookAt[0].latitude[0]),
        "lon": parseFloat(farm.LookAt[0].longitude[0])
    }

    var area_points = farm.Polygon[0].outerBoundaryIs[0].LinearRing[0].coordinates[0]
    area_points = area_points.substring(7);

    var area = area_points.split(" ");
    area.pop()

    area.forEach(function(part, index) {
      this[index] = area[index].split(",");
      this[index] = this[index].map(Number)
      this[index].pop()
    }, area);

    // Change lat lon position
    area.forEach(function(part, index) { 
        var lat = area[index][0]    
        var lon = area[index][1]
        area[index][0] = lon,    
        area[index][1] = lat
    }, area);

    return {
        "area": area, 
        "center_point": center_point,
        "cows": [],
        "route": []
    };
}

